import React, { useEffect, useState } from "react";
import "./App.css";
import Recipe from "./Recipe";

const App = () => {
  const APP_ID = "830cf297";
  const APP_KEY = "52c69f4b376519977a932b39db5eb444";

  const [recipes, setRecipes] = useState([]);
  const [search, setSearch] = useState('')
  // const [query, setQuery] = useState('chicken')

  useEffect(() => {
    getRecipies();
  }, [search]);

  const getRecipies = async () => {
    const response = await fetch(`https://api.edamam.com/search?q=${search}&app_id=${APP_ID}&app_key=${APP_KEY}`);
    const data = await response.json();
    setRecipes(data.hits);
    console.log(data.hits);
  };

  const updateSearch = (e) => {
    setSearch(e.target.value);
    console.log(search)
  }

  return (
    <div className="App">
      <h1>Ganesh</h1>
      <form className="search-form">
        <input className="search-bar" type="text" value={search} onChange={updateSearch}></input>
        <button className="searh-button" type="submit">
          Search
        </button>
      </form>
      {recipes.map(recipe => (
        <Recipe
          key={recipe.recipe.label}
          title={recipe.recipe.label}
          calories={recipe.recipe.calories}
          image={recipe.recipe.image}
        />
      ))}
    </div>
  );
};

export default App;
